def newIcon(icon, iconSize=None):
    if iconSize is not None:
        return QIcon(
            # QIcon(__iconpath__ + "/" + icon + ".png").pixmap(iconSize, iconSize)
            QIcon(":/" + icon).pixmap(iconSize, iconSize)
        )
    else:
        # return QIcon(__iconpath__ + "/" + icon + ".png")
        return QIcon(":/" + icon)
