TEST(CAS, SimplifyPower_0) {
  {  // x^0 = 1
    Var x   = ir::_Var_::Make("x", Float(32));
    auto p0 = ir::Power::Make(x, Expr(0));
    LOG(INFO) << "p0 " << p0;
